mapper.select:
select count(revNo)
    from review
        where itemNo = #{itemNo}

select count(revNo)
    from review
        where itemNo = #{itemNo} and revScore = ${revScore}

mapper.update:
update review
set revTxt = #{revTxt}, revFile = #{revFile}, revFileOri = #{revFileOri}
where revNo = ${revNo}
