mapper.select:
select userId from user
    where userName = #{userName}
        and userPhone = #{userPhone}
        and userBirth = DATE_FORMAT(#{userBirth}, '%Y-%m-%d')

select count(userPw) from user
    where userId = #{userId}
        and userName = #{userName}
        and userPhone = #{userPhone}
        and userBirth = DATE_FORMAT(#{userBirth}, '%Y-%m-%d')

select userId, userName, userPhone, userEmail, userBirth, point
    from user
        where userNo = #{userNo}

SELECT b.buyAuto, b.buyNo, b.buyDate, b.buyOpt, b.buyCode, i.itemImg, i.itemName
    FROM buy b
    JOIN item i ON b.itemNo = i.itemNo
        WHERE b.buyNo = (
                SELECT MAX(buyNo)
                    FROM buy WHERE userNo = #{userNo}
            );

SELECT count(DISTINCT buyNo)
    FROM buy
        WHERE userNo = ${userNo} and buyCode = #{buyCode}

mapper.update:
update user set userPw = #{userPw}
    where userId = #{userId}
        and userName = #{userName}
        and userPhone = #{userPhone}
        and userBirth = DATE_FORMAT(#{userBirth}, '%Y-%m-%d')

mapper.select:
SELECT b.buyAuto, b.buyNo, b.itemNo, b.buyCnt, b.buyOpt, b.buyCode, b.buyDate, i.itemImg, i.itemName FROM buy b JOIN item i ON b.itemNo = i.itemNo JOIN (SELECT buyNo FROM buy WHERE userNo = ${userNo}
AND buyCode = #{buyCode}
ORDER BY buyNo DESC limit ${limit}, 3) bb ON b.buyNo = bb.buyNo
where buyCode = #{buyCode}
order by buyNo DESC
